local killMob = function(mob, killId)
    local mobId = mob_obj_get(mob, killId)
    if (mobId > 0) then
        local mobX = mob_obj_x(mobId)
        local mobY = mob_obj_y(mobId)
        bot_stop()
        gotocoordinate(1, mobX, mobY)
        useskill(killId, 1)
        sleep(200)
        bot_start()
    end
end

